#' Cox Proportional Hazard Model
#'
#' 
#' @param data .
#' @param times .
#' @param status .
#' @param deps .
#' @param strata .
#' @param forestyn .
#' @param diagnosticsyn .
#' @param survivaltreeyn .
#' @param plotvar .
#' @param ciyn .
#' @param timeunits .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$modsurvTable1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$modsurvTable2} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$modsurvTable1$asDF}
#'
#' \code{as.data.frame(results$modsurvTable1)}
#'
#' @export
modellingSurvival <- function(
    data,
    times,
    status,
    deps,
    strata,
    forestyn = FALSE,
    diagnosticsyn = FALSE,
    survivaltreeyn = FALSE,
    plotvar,
    ciyn = FALSE,
    timeunits = "None") {

    if ( ! requireNamespace('jmvcore'))
        stop('modellingSurvival requires jmvcore to be installed (restart may be required)')

    if ( ! missing(times)) times <- jmvcore::resolveQuo(jmvcore::enquo(times))
    if ( ! missing(status)) status <- jmvcore::resolveQuo(jmvcore::enquo(status))
    if ( ! missing(deps)) deps <- jmvcore::resolveQuo(jmvcore::enquo(deps))
    if ( ! missing(strata)) strata <- jmvcore::resolveQuo(jmvcore::enquo(strata))
    if ( ! missing(plotvar)) plotvar <- jmvcore::resolveQuo(jmvcore::enquo(plotvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(times), times, NULL),
            `if`( ! missing(status), status, NULL),
            `if`( ! missing(deps), deps, NULL),
            `if`( ! missing(strata), strata, NULL),
            `if`( ! missing(plotvar), plotvar, NULL))


    options <- modellingSurvivalOptions$new(
        times = times,
        status = status,
        deps = deps,
        strata = strata,
        forestyn = forestyn,
        diagnosticsyn = diagnosticsyn,
        survivaltreeyn = survivaltreeyn,
        plotvar = plotvar,
        ciyn = ciyn,
        timeunits = timeunits)

    analysis <- modellingSurvivalClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
